package loopEx;

public class doWhileEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//do while : will execute at least once
		int i=0;
		do
		{
			System.out.println(i);
			i++;
		}while(i<0);
		
		i=0;
		while(i<0)
		{
			System.out.println(i);
			i++;
		}
		
	}

}
