package loopEx;

public class forEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0; i<5;i++)
		{
			System.out.println(i);
		}
		
		int a,b;
		a =0;
		b=0;
		System.out.println(a++); //post incrementer 0
		System.out.println(++b);//pre :

		System.out.println(a);//1
		System.out.println(b);//1
	}

}
