package Session_2_Questions;

public class QuesB76_2 {

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub

		for(int i=1; i<=40;i+=3)
		{
			System.out.print(i+" ");
		}
		System.out.println("\n");
		int j=1;
		while(j<=40)
		{
			System.out.print(j+" ");
			j=j+3;
		}
	}

}
